mapper.select:
SELECT 
FROM cm_activity


		AND name LIKE '%${condition.name}%'


		AND startTime <= #{condition.endTime} AND endTime >= #{condition.startTime}


		AND status = #{condition.status}


order by id desc

	LIMIT ${offset}, ${rows}

SELECT count(1)
FROM
cm_activity


		AND name LIKE '%${condition.name}%'


		AND startTime <= #{condition.endTime} AND endTime >= #{condition.startTime}


		AND status = #{condition.status}
