mapper.insert:
        INSERT INTO cm_activity_BusinessActivicty (
		    shopId
		    ,activityId
		    ,name
		    ,price
		    ,picture
		    ,score
		    ,description
		    ,stock
		    ,sale
		    ,startTime
		    ,endTime
		    ,rules
		    ,pNum
		    ,code
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{shopId}
		    	,#{activityId}
		    	,#{name}
		    	,#{price}
		    	,#{picture}
		    	,#{score}
		    	,#{description}
		    	,#{stock}
		    	,#{sale}
		    	,#{startTime}
		    	,#{endTime}
		    	,#{rules}
		    	,#{pNum}
		    	,#{code}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

