mapper.insert:
        INSERT INTO cm_auth_acl (
		    subjectType
		    ,subjectCode
		    ,resourceType
		    ,resourceCode
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{subjectType}
		    	,#{subjectCode}
		    	,#{resourceType}
		    	,#{resourceCode}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

