mapper.insert:
INSERT INTO cm_auth_module (
code
,name
,pCode
,pId
,url
,icon
,isMenu
,level
,fullCode
,fullName
,priority
,creator
,createDate
,lastModifier
,lastModDate
,status
) VALUES (
#{code}
,#{name}
,#{pCode}
,#{pId}
,#{url}
,#{icon}
,#{isMenu}
,#{level}
,#{fullCode}
,#{fullName}
,#{priority}
,#{creator}
,#{createDate}
,#{lastModifier}
,#{lastModDate}
,#{status}
)

    SELECT LAST_INSERT_ID() AS ID

