mapper.insert:
        INSERT INTO cm_auth_operation (
		    code
		    ,name
		    ,moduleCode
		    ,authCode
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{code}
		    	,#{name}
		    	,#{moduleCode}
		    	,#{authCode}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

