mapper.select:
		SELECT DATE_FORMAT(FROM_UNIXTIME(createTime/1000),'%d日') as name,count(id) as value
		From cm_bi_user_flow
		WHERE DATE_FORMAT(FROM_UNIXTIME(createTime/1000),'%Y') = #{year}
		AND date_format(FROM_UNIXTIME(createTime/1000),'%Y-%m') = date_format(date_sub(curdate(), interval #{month} month),'%Y-%m')
        AND date_format(FROM_UNIXTIME(createTime/1000),'%d') <= #{day}
		GROUP BY name

            order by name desc
            limit ${offset}, ${rows}

SELECT DATE_FORMAT(FROM_UNIXTIME(cbuf.createTime/1000),'%d日')as name, SUM(CASE WHEN cud.age >= #{minAge} AND cud.age < #{maxAge} THEN 1 ELSE 0 END) AS value FROM cm_bi_user_flow cbuf LEFT JOIN cm_user_detail cud ON cbuf.uid = cud.`code`
AND date_format(FROM_UNIXTIME(createDate/1000),'%Y') = #{year}
AND cbuf.eventType = 1 AND date_format(FROM_UNIXTIME(createDate/1000),'%Y-%m') = date_format(date_sub(curdate(), interval #{month} month),'%Y-%m')
AND date_format(FROM_UNIXTIME(createDate/1000),'%d') <= #{day}
GROUP BY name

SELECT DATE_FORMAT(FROM_UNIXTIME(cbuf.createTime/1000),'%m月')as name, count(cbuf.id) as value
FROM cm_bi_user_flow cbuf
WHERE cbuf.eventType = 1 and cbuf.uid is not null
AND DATE_FORMAT(FROM_UNIXTIME(createTime/1000),'%Y') = #{year}
AND DATE_FORMAT(FROM_UNIXTIME(createTime/1000),'%m') <= #{month}
GROUP BY name
ORDER BY name DESC

    limit ${offset}, ${rows}
