mapper.insert:
        INSERT INTO cm_business_goods (
		    goodsName
		    ,price
		    ,shopId
		    ,stock
		    ,sale
		    ,description
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{goodsName}
		    	,#{price}
		    	,#{shopId}
		    	,#{stock}
		    	,#{sale}
		    	,#{description}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE cm_business_goods
goodsName=#{goodsName}, price=#{price}, shopId=#{shopId}, stock=#{stock}, sale=#{sale}, description=#{description}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

