mapper.select:
SELECT cbg.*,csc.name as categoryName from cm_business_goods cbg LEFT JOIN cm_business_shop cbs
ON cbg.shopId = cbs.`code`
LEFT JOIN cm_sys_category csc
ON cbg.categoryId = csc.id


		AND cbg.shopId = #{condition.shopId}


		AND cbg.goodsName LIKE '%${condition.goodsName}%'


		AND cbg.price >= #{condition.plow} and cbg.price <= #{condition.phigh}


group by id
order by id desc

	limit ${offset}, #{rows}

SELECT count(1) from cm_business_goods cbg LEFT JOIN cm_business_shop cbs ON cbg.shopId = cbs.`code` LEFT JOIN cm_sys_category csc ON cbg.categoryId = csc.id
AND cbg.shopId = #{condition.shopId}
AND cbg.goodsName LIKE '%${condition.goodsName}%'
AND cbg.price >= #{condition.plow} and cbg.price <= #{condition.phigh}

mapper.update:
UPDATE cm_business_goods
set status = #{status}
where id = #{id}
