mapper.select:
SELECT cbg.*,csc.name as categoryName from cm_business_goods cbg LEFT JOIN cm_business_shop cbs
ON cbg.shopId = cbs.`code`
LEFT JOIN cm_sys_category csc
ON cbg.categoryId = csc.id


        AND cbg.shopId = #{condition.shopId}


        AND cbg.goodsName LIKE '%${condition.goodsName}%'


        AND cbg.price >= #{condition.pLow} and cbg.price <= #{condition.pHigh}


group by id
order by id desc

    limit ${offset}, #{rows}

SELECT count(1) from cm_business_goods cbg LEFT JOIN cm_business_shop cbs ON cbg.shopId = cbs.`code` LEFT JOIN cm_sys_category csc ON cbg.categoryId = csc.id
AND cbg.shopId = #{condition.shopId}
AND cbg.goodsName LIKE '%${condition.goodsName}%'
AND cbg.price >= #{condition.pLow} and cbg.price <= #{condition.pHigh}

mapper.update:
UPDATE cm_business_goods
set status = #{status}
where id = #{id}

update.foreach:
update cm_business_goods

    stock= ${item.stock}, sale = ${item.sale}

where code = #{item.code}

mapper.select:
SELECT DATE_FORMAT(FROM_UNIXTIME(createDate/1000),'%u周') as name,count(id) as value FROM cm_business_goods
AND DATE_FORMAT(FROM_UNIXTIME(createDate/1000),'%Y') = #{year}
AND status = 0 AND DATE_FORMAT(FROM_UNIXTIME(createDate/1000),'%u') = DATE_FORMAT(date_sub(curdate(), interval #{week} week),'%u')
AND DATE_FORMAT(FROM_UNIXTIME(createDate/1000),'%d') <= #{day}
GROUP BY name ORDER BY name DESC
