mapper.insert:
        INSERT INTO cm_business_indent (
		    uid
		    ,shopId
		    ,address
		    ,goodsId
		    ,goodsName
		    ,count
		    ,total
		    ,piece
		    ,isTraded
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{uid}
		    	,#{shopId}
		    	,#{address}
		    	,#{goodsId}
		    	,#{goodsName}
		    	,#{count}
		    	,#{total}
		    	,#{piece}
		    	,#{isTraded}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

