		    ,goodsId
		    ,num
		    ,price
        ) VALUES (
		    	#{indentId}
		    	,#{shopId}
		    	,#{goodsId}
		    	,#{num}
		    	,#{price}
        )

        	SELECT LAST_INSERT_ID() AS ID

