mapper.insert:
        INSERT INTO cm_business_shop (
		    shopName
		    ,address
		    ,province
		    ,city
		    ,town
		    ,cover
		    ,owner
		    ,uid
		    ,ownerPaperPic
		    ,ownerPaper
		    ,licensePic
		    ,license
		    ,shopCategoryId
		    ,linetTelephone
		    ,phone
		    ,income4Sum
		    ,withdrawable
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{shopName}
		    	,#{address}
		    	,#{province}
		    	,#{city}
		    	,#{town}
		    	,#{cover}
		    	,#{owner}
		    	,#{uid}
		    	,#{ownerPaperPic}
		    	,#{ownerPaper}
		    	,#{licensePic}
		    	,#{license}
		    	,#{shopCategoryId}
		    	,#{linetTelephone}
		    	,#{phone}
		    	,#{income4Sum}
		    	,#{withdrawable}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

