mapper.insert:
        INSERT INTO cm_cook_collection (
		    uid
		    ,cookbookId
		    ,createDate
		    ,creator
		    ,lastModDate
		    ,lastModifier
		    ,status
        ) VALUES (
		    	#{uid}
		    	,#{cookbookId}
		    	,#{createDate}
		    	,#{creator}
		    	,#{lastModDate}
		    	,#{lastModifier}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

