mapper.insert:
        INSERT INTO cm_cook_cookBook (
		    name
		    ,description
		    ,materialId
		    ,processId
		    ,categoryId
		    ,tips
		    ,praise
		    ,comment
		    ,uid
		    ,createDate
		    ,creator
		    ,lastModDate
		    ,lastModifier
		    ,status
        ) VALUES (
		    	#{name}
		    	,#{description}
		    	,#{materialId}
		    	,#{processId}
		    	,#{categoryId}
		    	,#{tips}
		    	,#{praise}
		    	,#{comment}
		    	,#{uid}
		    	,#{createDate}
		    	,#{creator}
		    	,#{lastModDate}
		    	,#{lastModifier}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

