mapper.insert:
        INSERT INTO cm_cook_material (
		    materialNames
		    ,dosage
		    ,createDate
		    ,creator
		    ,lastModDate
		    ,lastModifier
		    ,status
        ) VALUES (
		    	#{materialNames}
		    	,#{dosage}
		    	,#{createDate}
		    	,#{creator}
		    	,#{lastModDate}
		    	,#{lastModifier}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

