mapper.insert:
        INSERT INTO cm_share_comment (
		    content
		    ,userId
		    ,weiboId
		    ,articleId
		    ,fusionId
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{content}
		    	,#{userId}
		    	,#{weiboId}
		    	,#{articleId}
		    	,#{fusionId}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

