content=#{content}, userId=#{userId}, weiboId=#{weiboId}, articleId=#{articleId}, fusionId=#{fusionId}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

