mapper.insert:
        INSERT INTO cm_share_reply (
		    content
		    ,commentId
		    ,answerId
		    ,answerName
		    ,replayDate
		    ,replyToId
		    ,replyName
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{content}
		    	,#{commentId}
		    	,#{answerId}
		    	,#{answerName}
		    	,#{replayDate}
		    	,#{replyToId}
		    	,#{replyName}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

