mapper.insert:
        INSERT INTO cm_sys_category (
		    name
		    ,priority
		    ,type
		    ,status
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,creatorName
        ) VALUES (
		    	#{name}
		    	,#{priority}
		    	,#{type}
		    	,#{status}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{creatorName}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE cm_sys_category
name=#{name}, priority=#{priority}, type=#{type}, status=#{status}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, creatorName=#{creatorName},
WHERE id = #{id}

