mapper.select:
SELECT

FROM cm_sys_category


        AND name like '%${name}%'


        AND status = #{status}


        AND type = #{type}


GROUP BY Id
ORDER BY priority,createDate DESC

    limit ${offset}, ${rows}

SELECT count(1)
FROM cm_sys_category


        AND name like '%${name}%'


        AND status = #{status}


        AND type = #{type}

mapper.update:
UPDATE  cm_sys_category
SET name = #{param.name}, priority = #{param.priority}, type = #{param.type},
lastModifier = #{param.lastModifier}, lastModDate=#{param.lastModDate}
WHERE id = #{param.id}

UPDATE cm_sys_category
set status = #{status}
WHERE id = #{id}

mapper.delete:
DELETE FROM cm_sys_category
WHERE id in
#{item}
