mapper.insert:
        INSERT INTO cm_sys_verification (
		    verifiedCode
		    ,uid
		    ,type
		    ,createDate
		    ,disabledDate
		    ,state
		    ,creator
		    ,lastModifier
		    ,lastModDate
        ) VALUES (
		    	#{verifiedCode}
		    	,#{uid}
		    	,#{type}
		    	,#{createDate}
		    	,#{disabledDate}
		    	,#{state}
		    	,#{creator}
		    	,#{lastModifier}
		    	,#{lastModDate}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE cm_sys_verification
verifiedCode=#{verifiedCode}, uid=#{uid}, type=#{type}, createDate=#{createDate}, disabledDate=#{disabledDate}, state=#{state}, creator=#{creator}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate},
WHERE id = #{id}

