mapper.insert:
        INSERT INTO cm_user_address (
		    province
		    ,city
		    ,town
		    ,detail
		    ,uid
		    ,status
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
        ) VALUES (
		    	#{province}
		    	,#{city}
		    	,#{town}
		    	,#{detail}
		    	,#{uid}
		    	,#{status}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
        )

        	SELECT LAST_INSERT_ID() AS ID

