mapper.insert:
        INSERT INTO cm_user_Detail (
		    username
		    ,password
		    ,phone
		    ,address
		    ,nickname
		    ,sex
		    ,headIcon
		    ,funs
		    ,attentions
		    ,score
		    ,isStudent
		    ,studentId
		    ,studentPic
		    ,idCard
		    ,idCardPic
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{username}
		    	,#{password}
		    	,#{phone}
		    	,#{address}
		    	,#{nickname}
		    	,#{sex}
		    	,#{headIcon}
		    	,#{funs}
		    	,#{attentions}
		    	,#{score}
		    	,#{isStudent}
		    	,#{studentId}
		    	,#{studentPic}
		    	,#{idCard}
		    	,#{idCardPic}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

