mapper.insert:
        INSERT INTO cm_user_flow (
		    uid
		    ,event
		    ,sColumn
		    ,sContent
		    ,createTime
        ) VALUES (
		    	#{uid}
		    	,#{event}
		    	,#{sColumn}
		    	,#{sContent}
		    	,#{createTime}
        )

        	SELECT LAST_INSERT_ID() AS ID

